import styled from "styled-components";

const Wrapper = styled.div`
  height: 100%;
  margin-top: 150px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  .message {
    color: red;
    font-weight: bold;
  }
`;

export default Wrapper;
